html:
{% set fmt = frappe.utils.fmt_money %}
{% set cur = doc.currency or frappe.defaults.get_global_default("currency") %}
{% set in_words = (doc.total_in_words or frappe.utils.money_in_words(doc.rounded_total or doc.net_pay or 0, cur)) %}
{% set pay_period = (frappe.utils.formatdate(doc.start_date), frappe.utils.formatdate(doc.end_date)) %}
{% set show_ytd = ((doc.gross_year_to_date or 0) + (doc.year_to_date or 0)) > 0 %}

<style>
  * { box-sizing: border-box; }
  html, body { margin: 0; padding: 0; }
  body { font-family: Inter, Arial, ui-sans-serif, system-ui; color: #111827; }
  .wrap { width: 210mm; margin: 0 auto; padding: 10mm 10mm; }
  .tight { letter-spacing: .1px; }

  .header { display: grid; grid-template-columns: 1fr auto; align-items: end; gap: 8px; border-bottom: 1px solid #E5E7EB; padding-bottom: 6px; margin-bottom: 8px; }
  .brand { font-weight: 700; font-size: 16px; }
  .meta-small { font-size: 10px; color: #6B7280; }
  .slip-title { font-size: 18px; font-weight: 800; text-transform: uppercase; }

  .grid { display: grid; grid-template-columns: 1fr 1fr; gap: 8px 18px; margin: 8px 0; }
  .card { border: 1px solid #E5E7EB; border-radius: 8px; padding: 8px; }
  .card h4 { margin: 0 0 4px 0; font-size: 11px; text-transform: uppercase; color: #6B7280; }
  .dl { display: grid; grid-template-columns: 120px 1fr; gap: 3px 8px; font-size: 11px; }
  .mono { font-family: ui-monospace, SFMono-Regular, Menlo, Consolas, monospace; }
  .muted { color: #6B7280; }

  table { width: 100%; border-collapse: collapse; font-size: 11px; }
  .tbl { overflow: hidden; border: 1px solid #E5E7EB; border-radius: 8px; }
  .tbl thead th { background: #F9FAFB; text-align: left; padding: 6px 8px; font-weight: 700; border-bottom: 1px solid #E5E7EB; }
  .tbl tbody td { padding: 6px 8px; border-bottom: 1px solid #F3F4F6; }
  .tbl tfoot td { padding: 6px 8px; border-top: 1px solid #E5E7EB; font-weight: 700; background: #FAFAFA; }
  .tright { text-align: right; }
  .tcenter { text-align: center; }

  .summary { display: grid; grid-template-columns: 1fr 1fr; gap: 8px 18px; margin: 8px 0; }
  .pill { border: 1px dashed #D1D5DB; border-radius: 8px; padding: 8px; }
  .pill h3 { margin: 0 0 4px 0; font-size: 11px; color: #6B7280; text-transform: uppercase; }
  .highlight { font-size: 18px; font-weight: 800; }

  .footnote { font-size: 10px; color: #6B7280; margin-top: 8px; }
  .signs { display: grid; grid-template-columns: 1fr 1fr; gap: 18px; margin-top: 14px; }
  .sign { text-align: center; }
  .line { margin-top: 18px; height: 1px; background: #E5E7EB; }

  @media print {
    .wrap { padding: 8mm; }
    .header { margin-bottom: 6px; }
  }
</style>

<div class="wrap tight">
  <div class="letterhead">
  {{ frappe.get_doc("Letter Head", doc.letter_head).content if doc.letter_head else "" }}
</div>

  <div class="header">
    <div>
      <div class="brand">{{ doc.company }}</div>
      <div class="meta-small">
        {% if doc.branch %}Branch: {{ doc.branch }} · {% endif %}
        {% if doc.department %}Dept: {{ doc.department }}{% endif %}
      </div>
    </div>
    <div class="slip-title">Salary Slip</div>
  </div>

  <div class="grid">
    <div class="card">
      <h4>Employee</h4>
      <div class="dl">
        <div><b>Employee ID</b></div><div class="mono">{{ doc.employee }}</div>
        <div><b>Name</b></div><div>{{ doc.employee_name }}</div>
        <div><b>Designation</b></div><div>{{ doc.designation or "-" }}</div>
      </div>
    </div>
    <div class="card">
      <h4>Pay Period</h4>
      <div class="dl">
        <div><b>Period</b></div><div>{{ frappe.utils.formatdate(doc.start_date) }} — {{ frappe.utils.formatdate(doc.end_date) }}</div>
        <div><b>Payroll Frequency</b></div><div>{{ doc.payroll_frequency or "-" }}</div>
        <div><b>Pay Date</b></div><div>{{ frappe.utils.formatdate(doc.posting_date) if doc.posting_date else "-" }}</div>
      </div>
    </div>
  </div>

  <div class="summary">
    <div class="pill">
      <h3>Attendance</h3>
      <div class="dl">
        <div><b>Total Days</b></div><div>{{ (doc.total_working_days or 0) | round(2) }}</div>
        <div><b>Payment Days</b></div><div>{{ (doc.payment_days or 0) | round(2) }}</div>
        <div><b>LWP</b></div><div>{{ (doc.leave_without_pay or 0) | round(2) }}</div>
        <div><b>Absent</b></div><div>{{ (doc.absent_days or 0) | round(2) }}</div>
      </div>
    </div>
    <div class="pill">
      <h3>Net Pay</h3>
      <div class="highlight mono">{{ fmt((doc.rounded_total or doc.net_pay or 0), currency=cur) }}</div>
      <div class="muted" style="margin-top: 2px;">({{ in_words }})</div>
    </div>
  </div>

  <div class="grid" style="margin-top: 4px;">
    <div class="tbl">
      <table>
        <thead>
          <tr>
            <th colspan="{{ 2 + (1 if show_ytd else 0) }}">Earnings</th>
          </tr>
          <tr>
            <th>Component</th>
            <th class="tright">Amount</th>
            {% if show_ytd %}<th class="tright">YTD</th>{% endif %}
          </tr>
        </thead>
        <tbody>
          {% set earnings = (doc.earnings or []) | selectattr("amount", "defined") | selectattr("amount", "ne", 0) | list %}
          {% if earnings|length == 0 %}
            <tr><td colspan="{{ 2 + (1 if show_ytd else 0) }}" class="tcenter muted">—</td></tr>
          {% else %}
            {% for row in earnings %}
              <tr>
                <td>{{ row.salary_component or row.abbr }}</td>
                <td class="tright mono">{{ fmt(row.amount or 0, currency=cur) }}</td>
                {% if show_ytd %}<td class="tright mono">{{ fmt(row.year_to_date or 0, currency=cur) }}</td>{% endif %}
              </tr>
            {% endfor %}
          {% endif %}
        </tbody>
        <tfoot>
          <tr>
            <td>Total Earnings</td>
            <td class="tright mono">{{ fmt((doc.gross_pay or doc.total_earnings or 0), currency=cur) }}</td>
            {% if show_ytd %}<td class="tright mono">{{ fmt(doc.gross_year_to_date or 0, currency=cur) }}</td>{% endif %}
          </tr>
        </tfoot>
      </table>
    </div>

    <div class="tbl">
      <table>
        <thead>
          <tr>
            <th colspan="{{ 2 + (1 if show_ytd else 0) }}">Deductions</th>
          </tr>
          <tr>
            <th>Component</th>
            <th class="tright">Amount</th>
            {% if show_ytd %}<th class="tright">YTD</th>{% endif %}
          </tr>
        </thead>
        <tbody>
          {% set deductions = (doc.deductions or []) | selectattr("amount", "defined") | selectattr("amount", "ne", 0) | list %}
          {% if deductions|length == 0 %}
            <tr><td colspan="{{ 2 + (1 if show_ytd else 0) }}" class="tcenter muted">—</td></tr>
          {% else %}
            {% for row in deductions %}
              <tr>
                <td>{{ row.salary_component or row.abbr }}</td>
                <td class="tright mono">{{ fmt(row.amount or 0, currency=cur) }}</td>
                {% if show_ytd %}<td class="tright mono">{{ fmt(row.year_to_date or 0, currency=cur) }}</td>{% endif %}
              </tr>
            {% endfor %}
          {% endif %}
        </tbody>
        <tfoot>
          <tr>
            <td>Total Deductions</td>
            <td class="tright mono">{{ fmt(doc.total_deduction or 0, currency=cur) }}</td>
            {% if show_ytd %}<td class="tright mono">{{ fmt(0, currency=cur) }}</td>{% endif %}
          </tr>
        </tfoot>
      </table>
    </div>
  </div>

  {% if doc.bank_name or doc.bank_account_no or doc.mode_of_payment %}
  <div class="card" style="margin-top: 6px;">
    <h4>Payment</h4>
    <div class="dl">
      <div><b>Method</b></div><div>{{ doc.mode_of_payment or "-" }}</div>
      <div><b>Bank</b></div>
      <div>
        {% if doc.bank_name %}{{ doc.bank_name }}{% if doc.bank_account_no %} — {{ doc.bank_account_no }}{% endif %}
        {% else %}-{% endif %}
      </div>
    </div>
  </div>
  {% endif %}

  <div class="footnote">
    This is a computer-generated salary slip. For any discrepancy, please contact HR/Payroll within 7 working days.
  </div>
  <div class="signs">
    <div class="sign">
      <div class="line"></div>
      <small>Authorised Signatory</small>
    </div>
    <div class="sign">
      <div class="line"></div>
      <small>Employee Signature</small>
    </div>
  </div>
</div>
